import '../index.css';
import podcastImage from '../assets/podcast.png'; // Adjust the path as necessary
import ProjectSlider from './ProjectSlider';

import React, { useState } from 'react';
import podcastImage1 from '../assets/podcast1.jpg';
import podcastImage2 from '../assets/podcast2.jpg';
import podcastImage3 from '../assets/podcast3.jpg';
import './ProjectSlider.css';

const Projects = () => {
    const slides = [
        {
            image: podcastImage1,
            title: "AITU Podcast 1",
            description: "Университетский проект студентов, направленный на медиа развитие университета в сфере подкастинга и YouTube контент-мейкинга",
            link: "https://www.instagram.com/mediastudio.kz?igsh=c2VmNGgwOTIzeWx2"
        },
        {
            image: podcastImage2,
            title: "AITU Podcast 2",
            description: "Университетский проект студентов, направленный на медиа развитие университета в сфере подкастинга и YouTube контент-мейкинга",
            link: "https://www.instagram.com/mediastudio.kz?igsh=c2VmNGgwOTIzeWx2"
        },
        {
            image: podcastImage3,
            title: "AITU Podcast 3",
            description: "Университетский проект студентов, направленный на медиа развитие университета в сфере подкастинга и YouTube контент-мейкинга",
            link: "https://www.instagram.com/mediastudio.kz?igsh=c2VmNGgwOTIzeWx2"
        }
    ];

    const [currentSlide, setCurrentSlide] = useState(0);

    const nextSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
    };

    const prevSlide = () => {
        setCurrentSlide((prevSlide) => (prevSlide - 1 + slides.length) % slides.length);
    };

    return (

        <section className="project-zone">
            <section className="projects" id='projects'>
            <h2>ПРОЕКТЫ</h2>

        </section>
            <div className="carousel-project">
                <img src={slides[currentSlide].image} alt={slides[currentSlide].title} />
                <div className="carousel-controls">
                    <button className="prev" onClick={prevSlide}><i className="fi fi-br-angle-left"></i></button>
                    <button className="next" onClick={nextSlide}><i className="fi fi-bs-angle-right"></i></button>
                </div>
                <div className="carousel-caption-project">
                    <div className="caption-text">
                        <h3>{slides[currentSlide].title}</h3>
                        <p>{slides[currentSlide].description}</p>
                    </div>
                    <a href={slides[currentSlide].link} className="project-book">Смотреть</a>
                </div>
            </div>
        </section>
    );
};

export default Projects;
